import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      friends: [],
      nome: '',
      descricao: ''
    };
    this.lista = ""

    this.create = this.create.bind(this);
    this.update = this.update.bind(this);
    this.delete = this.delete.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    // get all entities - GET
  }

  create(e) {
    // add entity - POST
    e.preventDefault();
  }

  update(e) {
    // update entity - PUT
    e.preventDefault();

  }

  delete(e) {
    // delete entity - DELETE
    e.preventDefault();
  }

  listando(){
    fetch("http://localhost:8080/lista", {"method":"GET"}, 
    {"headers": 
    {'Access-Control-Allow-Origin': '*',
    'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, PATCH',
    'Access-Control-Allow-Headers': 'Origin, X-Requested-With, Content-Type, Accept, Authorization',}})
    .then(Response => this.lista = Response.text.replaceAll("{", "").replaceAll("}",""))
    .then(Response => {console.log(Response);})
    .catch(err => {console.log(err); });
  }

  handleChange(changeObject) {
    this.setState(changeObject)
  }

  render() {
    return (
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-md-8">
              <h1 className="display-4 text-center">Chamada por React</h1>
              <form className="d-flex flex-column">
                <legend className="text-center">Adicionar-Atualizar-Remover</legend>
                <label htmlFor="nome">
                  Nome:
                  <input
                    name="nome"
                    id="nome"
                    type="text"
                    className="form-control"
                    value={this.state.name}
                    onChange={(e) => this.handleChange({ nome: e.target.value })}
                    required
                    />
                </label>
                <label htmlFor="descricao">
                Descrição:
                  <input
                    name="descricao"
                    id="descricao"
                    type="test"
                    className="form-control"
                    value={this.state.descricao}
                    onChange={(e) => this.handleChange({ descricao: e.target.value })}
                    required
                    />
                </label>
                <button className="btn btn-secondary" type='button' onClick={(e) => this.listando()}>
                    Listar
                </button>
                <button className="btn btn-primary" type='button' onClick={(e) => this.create(e)}>
                  Adicionar
                </button>
                <button className="btn btn-info" type='button' onClick={(e) => this.update(e)}>
                    Atualizar
                </button>
                <button className="btn btn-danger" type='button' onClick={(e) => this.delete(e)}>
                    Remover
                </button>
                <text value={this.lista}></text>
              </form>
            </div>
          </div>
        </div>
    );
  }
}
class Friends extends React.listando {
    render() {
        return (
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Since</th>
                    </tr>
                </thead>
                <tbody>
                    {this.props.friends && this.props.friends.map(friend => {
                        return <tr>
                            <td>{friend._id}</td>
                            <td>{friend.name}</td>
                            <td>{friend.since}</td>
                        </tr>
                    })}
                </tbody>
            </table>
        );
    }
}
export default App
