import React from 'react';
import ReactDOM from 'react-dom'


class Friends extends React.Component {
    render() {
        return (
            <table>
                <thead>
                    <tr>
                        <th>Nome</th>
                        <th>Descrição</th>
                        <th>Data de Incrição</th>
                        <th>Ultima Atualização</th>
                    </tr>
                </thead>
                <tbody>
                    {this.props.Nome && this.props.Nome.map(Nome => {
                        return <tr>
                            <td>{Nome.nome}</td>
                            <td>{Nome.descricao}</td>
                            <td>{Nome.DatadeInicio}</td>
                            <td>{Nome.descricao}</td>
                        </tr>
                    })}
                </tbody>
            </table>
        );
    }
}

class App1 extends React.Component {
    constructor(props) {
        super(props);
    this.state = {
      friends: [],
      name: '',
      id: '',
      notes: ''
    };

    this.create = this.create.bind(this);
    this.update = this.update.bind(this);
    this.delete = this.delete.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

   componentDidMount() {
    fetch("http://localhost:8080/lista", {
      "method": "GET",
       "headers": {
      }
    })
    .then(response => response.json())
        .then(response => {
           this.setState({
             friends: response
           })
         })
         .catch(err => { console.log(err); 
         });
   }

  create(e) {
    e.preventDefault();

    fetch("http://localhost:8080/add", {
      "method": "POST",
      "headers": {
        "content-type": "application/json",
        "accept": "application/json"
      },
      "body": JSON.stringify({
        name: this.state.name,
        notes: this.state.notes
      })
    })
    .then(response => response.json())
    .then(response => {
      console.log(response)
    })
    .catch(err => {
      console.log(err);
    });
  }

  update(e) {
    e.preventDefault();

    fetch("http://localhost:8080/update", {
        "method": "POST",
        "headers": {
            "content-type": "application/json",
            "accept": "application/json"
        },
        "body": JSON.stringify({
            _id: this.state.id,
            name: this.state.name,
            notes: this.state.notes
        })
        })
        .then(response => response.json())
        .then(response => { console.log(response);
        })
        .catch(err => { console.log(err); });
  }

  delete(e) {
    e.preventDefault();
    fetch(`http://localhost:8080/delete`, {
      "method": "POST",
      "headers": {
       }
    })
    .then(response => response.json())
    .then(response => {
      console.log(response);
    })
    .catch(err => {
      console.log(err);
    });
  }

  handleChange(changeObject) {
    this.setState(changeObject)
  }

  render() {
    const { nomes } = this.state;
    console.log(nomes)
    return (
        
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-md-8">
              <form className="d-flex flex-column">
                <legend className="text-center">Adição-Update-Delete</legend>
                <label htmlFor="name">
                  Nome:
                  <input
                    name="name"
                    id="name"
                    type="text"
                    className="form-control"
                    value={this.state.name}
                    onChange={(e) => this.handleChange({ name: e.target.value })}
                    required
                    />
                </label>
                <label htmlFor="notes">
                  Descrição:
                  <input
                    name="notes"
                    id="notes"
                    type="test"
                    className="form-control"
                    value={this.state.notes}
                    onChange={(e) => this.handleChange({ notes: e.target.value })}
                    required
                    />
                </label>
                
                <button className="btn btn-primary" type='button' onClick={(e) => this.create(e)}>
                  Add
                </button>
                <button className="btn btn-info" type='button' onClick={(e) => this.update(e)}>
                    Update
                </button>
                <button className="btn btn-danger" type='button' onClick={(e) => this.delete(e)}>
                    Delete
                </button>
                <Friends friends={this.state.friends} />
              </form>
            </div>
          </div>
        </div>
    );
  }
}

let domContainer = document.querySelector('#root');
ReactDOM.render(<App1/>, domContainer);
export default App1
